mapper.select:

select id, cid, xh, jkd, rl, dy, nz, wd, zfd, ctime, `uid`, zgwd, zdwd, cdcs, xslc
from dc
where id = #{id,jdbcType=INTEGER}


select id, cid, xh, jkd, rl, dy, nz, wd, zfd, ctime, `uid`, zgwd, zdwd, cdcs, xslc
from dc



    uid = #{uid}


    and cid = #{cid}



order by ctime desc
